import React, { Component } from 'react';
import './App.css';
import Todo from './Todo';
import NewTodo from './NewTodo';

var apiKey = "6e2ac39490d68d8cee32b36012960fb0a3e39cfec2f17767e25323f7292f60ee";

class App extends Component {

  constructor() {
    super()
    // stuff here
    this.state = {
      todos: [],
      //somethingElse: ''
    }
    this.addTodo = this.addTodo.bind(this);
  }

  // this.setState({
  //   todos: [...this.state.todos, newTodo]
  // });

  addTodo(event) {
    event.preventDefault();

    //const newTodoText = this.state.input;
    //Do Ajax

    var self = this;
    var createRequest = new XMLHttpRequest();
    createRequest.onreadystatechange = function () {
      if (this.readyState === 4 && this.status === 200) {
        // save new Todo to state
        self.setState({
          todos: [...self.state.todos, JSON.parse(this.responseText)]
        })
        // clear the input field
        self.setState({input: ''});
      }
    }

    createRequest.open("POST", "https://api.kraigh.net/todos", true);

    createRequest.setRequestHeader("Content-type", "application/json");
    createRequest.setRequestHeader("x-api-key", apiKey);
    //createRequest.send(JSON.stringify(data));

    // console.log(event.target);
    // console.log(this.props.id);
    // console.log(this.state.todos);
  }

  onChange(event) {
    //Set the state to the value of the Input
    this.setState({
      input: event.target.value
    });
  }


  //Load existing todos
  componentDidMount() {
    const self = this;

    var listRequest = new XMLHttpRequest();
    listRequest.onreadystatechange = function() {
      if(this.readyState == 4 && this.status == 200) {

        var todos = JSON.parse(this.responseText);
        self.setState({todos: todos});
        // //Display Todos on page
        // for(var i = 0; i < todos.length; i++) {
        //   renderTodo(todos[i]);
        // }

      }
      else if(this.readyState == 4) {
        console.log(this.responseText);
      }

    }
    listRequest.open("GET", "https://api.kraigh.net/todos", true);
    listRequest.setRequestHeader("x-api-key", apiKey);
    listRequest.send();

  }


  render() {
    return (
      <div class="container-fluid">
        <div class="row justify-content-center">
          <div class="header">
            <div class="text-header">
              <h1 class="text-center" id="title">My To-Do List</h1>
                <p class="lead text-center" id="second-text">Do not procrastinate. Ever</p>
            </div>

            <div class="container">
              //<NewTodo />

              <NewTodo addTodo={this.newTodo} onChange={this.onChange} input={this.state.input} />

              <div class="todos">
                <ul id="todos" class="list-unstyled">
                  //<Todo />
                  {this.state.todos.map((todo) =>
                    <Todo key={todo.id} id={todo.id} completed={todo.completed}
                      text={todo.text} removeDeletedTodo={this.removeDeletedTodo}/>
                  )}

                </ul>

              </div>

            </div>

          </div>

        </div>

      </div>
    );
  }
}

export default App;
