import React, { Component } from 'react';
import './App.css';
import Todo from './Todo';
import NewTodo from './NewTodo';

var apiKey = "6e2ac39490d68d8cee32b36012960fb0a3e39cfec2f17767e25323f7292f60ee";

class App extends Component {

  constructor(props) {
    super(props)
    // stuff here
    this.state = {
      todos: [],
      input: ' '
    }
    this.addTodo = this.addTodo.bind(this);
    this.onChange = this.onChange.bind(this);
  }


  addTodo(event) {
    console.log('addTodo')
    event.preventDefault();
    //Do Ajax
    var self = this;
    const newTodoText = self.state.input;

    var createRequest = new XMLHttpRequest();
    createRequest.onreadystatechange = function () {
      if (this.readyState === 4 && this.status === 200) {
        console.log("addTodo button running...");
        /* clear the input field */
        this.setState(todos => ({
          todos: [...this.state.todos, newTodoText]
        }));
        self.setState({input: ''});
      }
      else {
        console.log(this.responseText);
      }
    }

    createRequest.open("POST", "https://api.kraigh.net/todos", true);
    createRequest.setRequestHeader("Content-type", "application/json");
    createRequest.setRequestHeader("x-api-key", apiKey);
    createRequest.send(JSON.stringify(newTodoText));

    // console.log(event.target);
    // console.log(this.props.id);
    // console.log(this.state.todos);
  }


  onChange(event) {
    this.setState({
      input: event.target.value
    });
  }


  deleteTodo(event) {
    var todoID = event.currentTarget.parentNode.id;
    var self = this;

    //API call, DELETE to remove

    var deleteRequest = new XMLHttpRequest();

    deleteRequest.onreadystatechange = function() {
      if(this.readyState === 4 && this.status === 200) {
        //event.target.parentNode.parentNode.remove();
        const remainingTodos = self.state.todos.filter((todo) => {
          // Looping through all todos, if the id of the current todo DOES NOT equal the id of the todo we want to delete, keep it
          if (todo.id !== todoID) {
            return todo;
          }
          self.setState({todos: todo});
        });

      }
      else if (this.readyState === 4) {
        console.log(this.responseText);
      }
    }

    deleteRequest.open("DELETE", "https://api.kraigh.net/todos/" + todoID, true);
    deleteRequest.setRequestHeader("Content-type", "application/json");
    deleteRequest.setRequestHeader("x-api-key", apiKey);
    deleteRequest.send();
  }


  //Load existing todos
  componentDidMount() {
    const self = this;

    var listRequest = new XMLHttpRequest();
    listRequest.onreadystatechange = function() {
      if(this.readyState === 4 && this.status === 200) {

        var todos = JSON.parse(this.responseText);
        self.setState({todos: todos});

      }
      else if(this.readyState === 4) {
        console.log(this.responseText);
      }

    }
    listRequest.open("GET", "https://api.kraigh.net/todos", true);
    listRequest.setRequestHeader("x-api-key", apiKey);
    listRequest.send();

  }


  render() {
    return (
      <div className="container-fluid">
        <div className="row justify-content-center">
          <div className="header">
            <div className="text-header">
              <h1 className="text-center" id="title">My To-Do List</h1>
                <p className="lead text-center" id="second-text">Do not procrastinate. Ever</p>
            </div>

            <div className="container">

              <NewTodo addTodo={this.newTodo} onChange={this.onChange} input={this.state.input} />

              <div className="todos">
                <ul id="todos" className="list-unstyled">

                  {this.state.todos.map((todo) =>
                    <Todo key={todo.id} id={todo.id} completed={todo.completed}
                      text={todo.text} removeDeletedTodo={this.removeDeletedTodo}/>
                  )}

                </ul>

              </div>

            </div>

          </div>

        </div>

      </div>
    );
  }
}

export default App;
